<script>
    $(document).ready(function () {
        $("#firstName").show();
        $("#lastName").show();
        var contentType = ["Room", "Restaurant", "Bar", "Reception", "HouseKeeping", "ConferenceArea", "WellnessSpa"];
        $("#ratinganddescription").html("");
        $("#ratingheader").hide();

        for (i = 0; i < contentType.length; i++) {
            $("#ratinganddescription").append('<div id="' + contentType[i] + 'Content" class="ratinganddescriptionContent"></div>');
            for (j = 5; j > 0; j--) {
                $("#" + contentType[i] + "Content").append('<input type="radio" name="' + contentType[i] + '.Rating" id="rate-' + contentType[i] + j + '" value="' + j + '">' +
                    '<label for= "rate-' + contentType[i] + j + '" class= "fas fa-star"></label >');
            }
            $("#" + contentType[i] + "Content").append('<div class="form-group"><textarea class="form-control" id="' + contentType[i] + 'Description"  name="' + contentType[i] + '.Description" rows="3" placeholder="Add a comment (optional)"></textarea><span id="' + contentType[i] + 'delete" onclick="deletereview(' + i + ')" style="cursor: pointer;text-decoration: underline;color: red;float: right;">Delete review</span><br/></div>');
        }
        var ratingdiv = $("#WellnessSpaContent,#ConferenceAreaContent,#HouseKeepingContent,#ReceptionContent,#BarContent,#RestaurantContent,#RoomContent");
        $(ratingdiv).hide();
    });
    $(".feedbackcards").click(function () {
        var cardId = this.id;
        $("#ratinganddescriptiondisplay").hide();
        $("#alltheratings").hide();
        var ratingdiv = $("#WellnessSpaContent,#ConferenceAreaContent,#HouseKeepingContent,#ReceptionContent,#BarContent,#RestaurantContent,#RoomContent");
        $(ratingdiv).hide();
        $("#" + cardId + "Content").show();
        $("#ratingtitle").text(cardId);
        $("#ratingsubtitle").text('Please evaluate using the stars.');
        $("#ratingheader").show();
        $("#ratingbtns").show();
        //$("#ratinghomebtn").hide();
        let ele = document.getElementById('ratinghomebtn');
    	ele.setAttribute ('style', 'display: none !important;');
    });

    $(".showoverallrating").click(function () {
        var ratingdiv = $("#WellnessSpaContent,#ConferenceAreaContent,#HouseKeepingContent,#ReceptionContent,#BarContent,#RestaurantContent,#RoomContent");
        $(ratingdiv).hide();
        $("#ratinganddescriptiondisplay").hide();
        $("#ratingheader").show();
        var contentType = ["Room", "Restaurant", "Bar", "Reception", "HouseKeeping", "ConferenceArea", "WellnessSpa"];

        $("#ratingtitle").text('My Reviews');
        $("#ratingsubtitle").text('All your ratings will be shown here. Go back to review your first category.');
        for (i = 0; i < contentType.length; i++) {
            $("." + contentType[i] + "rating").html("");
            let rates = document.getElementsByName("" + contentType[i] + ".Rating");
            for (j = 0; j < rates.length; j++) {
                if (rates[j].checked) {
                    $("." + contentType[i] + "rating").show();
                    var count = rates[j].value;
                    //$("#alltheratings").append('<div id="' + contentType[i] + 'rating" class="feedbackcards p-3">' + contentType[i] + '</div><hr />');
                    $("." + contentType[i] + "rating").append('' + contentType[i] + '')
                    for (k = 0; k < count; k++) {
                        $("." + contentType[i] + "rating").append('<i class="fas fa-star" style="color:gold;float: right;"></i>');
                    }
                }
            }
        }
        $("#alltheratings").show();
    });

    $(".back").click(function () {
        var ratingdiv = $("#WellnessSpaContent,#ConferenceAreaContent,#HouseKeepingContent,#ReceptionContent,#BarContent,#RestaurantContent,#RoomContent");
        $(ratingdiv).hide();
        $("#ratingheader").hide();
        $("#ratingbtns").hide();
        $("#alltheratings").hide();
        //$("#ratinghomebtn").show();
         let ele = document.getElementById('ratinghomebtn');
    	ele.setAttribute ('style', 'display: block !important;');
        $("#ratinganddescriptiondisplay").show();

        var contentType = ["Room", "Restaurant", "Bar", "Reception", "HouseKeeping", "ConferenceArea", "WellnessSpa"];
        for (i = 0; i < contentType.length; i++) {
            let rates = document.getElementsByName("" + contentType[i] + ".Rating");
            for (j = 0; j < rates.length; j++) {
                if (rates[j].checked) {
                    $("#" + contentType[i] + " i").css("color", "green");
                }
            }

        }
    });

    function deletereview(i) {
        debugger;
        var contentType = ["Room", "Restaurant", "Bar", "Reception", "HouseKeeping", "ConferenceArea", "WellnessSpa"];

        let rates = document.getElementsByName("" + contentType[i] + ".Rating");
        for (j = 0; j < rates.length; j++) {
            if (rates[j].checked) {
                $(rates[j]).prop('checked', false);
                $("." + contentType[i] + "rating").hide();
            }
        }

    }
</script>


