    function SendMail(name, email, phoneNumber) {
        if (email == '' || email == null || !validateEmail(email)) {
            $('#subscribe-email-validation').show();
        } else if (name == '' || name == null) {
            $('#subscribe-name-validation').show();
        } else {
            $('#subscribe-email-validation').hide();
            $('#subscribe-name-validation').hide();
            var dataValue = "{ 'Name':'" + name + "','Email':'" + email + "','Phone':'" + phoneNumber + "' }";
            $.ajax({
                type: "POST",
                url: '/home/PushSubscription/',
                //url: "PushSubscription/Footer",
                contentType: "application/json; charset=utf-8",
                data: dataValue,
                success: function (data) {
                    document.getElementById('subscriptionResponse').innerHTML = data.res;
                    if (data.res.toString().includes("Error occured")) {
                        document.getElementById('subscriptionResponse').style.color = "red";
                    } else {
                         document.getElementById('subscriptionResponse').style.color = "green";
                    }
                }
            });
